import React, { useState } from "react";
import NewItem from "./components/NewItem/newItem";
import ItemList from "./components/ItemList/itemList";
import DateRange from "./components/DateRangePicker/dateRange";
import "./App.css";

function App() {
  this.state = {
    tData: [],
    selectedStudent: "",
    studentList: [],
    selectStudentTable: [],
    date: date,
    entry: "",
    exit: "",
    startDay: "",
    endDay: "",
    dateDiff: "",
    attNote: "",
    trainerNote: "",
    attachments: "",
    status: "",
    hours1: "",
    min1: "",
    hours2: "",
    min2: "",
    sec1: "",
    sec2: "",
    timeDiff: "",
    catchDate: false
  };
  const [defaultItems, setItems] = useState([
    { id: 1, text: "Food & Drinks", spend: "" },
    { id: 2, text: "entertainment & Fun", spend: "" },
    { id: 3, text: "Trainning & Study", spend: "" },
    { id: 4, text: "Clothes & Shoes", spend: "" }
  ]);

  const onListHandle = newItem => {
    setItems(prevItems => prevItems.concat(newItem));
  };

  const getDateDiff = (dateDiff, startDay, endDay) => {
    this.setState({
      dateDiff: dateDiff,
      startDay: startDay,
      endDay: endDay
    });
    var tempData = [];

    this.finalTable = [];
    console.log("diff", this.state.dateDiff);
    console.log("start", this.state.startDay);
    console.log("end", this.state.endDay);

    if (endDay !== undefined) {
      for (let i = 0; i < this.state.dateDiff; i++) {
        tempData.push({
          date: moment(this.state.startDay).format("DD.MM.YYYY"),
          entry: null,
          attNote: null,
          attachments: null
        });
        console.log("diff", this.state.dateDiff);
        console.log("a1", [...tempData]);
        this.setState({
          tData: [...tempData]
        });
        console.log("a2", this.state.tData);
        //for loop the real-time clicked data
        if (startDay !== null) {
          startDay.setDate(startDay.getDate() + 1);
        } else {
          let today = new Date();
          return today;
        }
      }
    } else {
      tempData.push({
        date: moment(this.state.startDay).format("DD.MM.YYYY"),
        entry: null,
        attNote: null,
        attachments: null
      });
      console.log("a1", [...tempData]);
      this.setState({
        tData: [...tempData]
      });
      console.log("a2", this.state.tData);
    }

    this.attendance = [];
    let match;
    let cloneData = [...this.state.tData];
    //to map the function
    let realDates = this.state.tData.map((realDate, i) => {
      match = this.state.user.attendance.filter(val => {
        return val.date === realDate.date;
      });
      console.log("realClick", realDate.date);

      if (match.length > 0) {
        cloneData[i] = { ...match[0] };
        this.setState({ tData: [...cloneData] });
        console.log("final dummy data", [...this.state.tData]);
      }

      let selectEntryTimeResult;
      if (this.state.user.role == "trainer") {
        selectEntryTimeResult = (
          <EntryTimeSelect
            onSave={this.HandleEnterTimeReset}
            onChange={this.HandleEnterTimeReset}
            entry={this.state.tData[i].entry}
            value={realDate.date} //to match the rel click to the cliked date
          />
        );
      } else if (this.state.user.role == "manager") {
        selectEntryTimeResult = (
          <EntryTimeSelect
            onSave={this.HandleEnterTimeReset}
            onChange={this.HandleEnterTimeReset}
            entry={this.state.tData[i].entry}
            value={realDate.date} //to match the rel click to the cliked date
          />
        );
      } else {
        selectEntryTimeResult = this.state.tData[i].entry;
      }

      let studentNoteResult;
      if (this.state.user.role == "attendee") {
        studentNoteResult = (
          <AttendenceNote
            onClick={this.HandleAttendenceNote}
            onClose={this.HandleAttendenceNote}
            onChange={this.HandleAttendenceNote}
            attNote={this.state.tData[i].attNote}
            onCancel={this.HandleAttendenceNote}
            value={realDate.date} //from getDateDiff event
          />
        );
      } else {
        studentNoteResult = this.state.tData[i].attNote;
      }

      let uploadResult;
      if (this.state.user.role == "attendee") {
        uploadResult = (
          <Upload
            onClick={this.HandleAttachment}
            onChange={this.HandleAttachment}
            attachments={this.state.tData[i].attachments}
            value={realDate.date}
          />
        );
      } else if (this.state.user.role == "trainer") {
        uploadResult = (
          <Upload
            onClick={this.HandleAttachment}
            onChange={this.HandleAttachment}
            attachments={this.state.tData[i].attachments}
            value={realDate.date}
          />
        );
      } else if (this.state.user.role == "manager") {
        uploadResult = (
          <Upload
            onClick={this.HandleAttachment}
            onChange={this.HandleAttachment}
            attachments={this.state.tData[i].attachments}
            value={realDate.date}
          />
        );
      } else {
        uploadResult = this.state.tData[i].attachments;
      }

      this.finalTable.push(
        <tr key={i} align="center">
          <td>{this.state.tData[i].date} </td>
          <td>{selectEntryTimeResult}</td>
          <td>{studentNoteResult}</td>
          <td>{uploadResult}</td>
        </tr>
      );

      // startDay.setDate(startDay.getDate() + 1);
      this.setState({
        catchDate: true
      });
      console.log("realDate", realDate.date);
    });
  };

  return (
    <div className="App">
      <h1>Welcome , easily record your daily spending here!</h1>
      <DateRange></DateRange>
      <NewItem onAddItem={onListHandle}></NewItem>
      <ItemList items={defaultItems}></ItemList>
      <td>{this.finalTable}</td>
    </div>
  );
}

export default App;
