import React from "react";
import { BrowserRouter as Router, Link, Route } from "react-router-dom";
import Dashboard from "./components/Dashboard/dashboard";
import Main from "./components/main";
import Navbar from "./components/navbar";
import "./App.css";

function App() {
  let openNav = () => {
    if (
      document.getElementById("mySidenav") &&
      document.getElementById("main")
    ) {
      document.getElementById("mySidenav").style.width = "250px";
      document.getElementById("main").style.marginLeft = "250px";
    }
  };

  let closeNav = () => {
    if (
      document.getElementById("mySidenav") &&
      document.getElementById("main")
    ) {
      document.getElementById("mySidenav").style.width = "0";
      document.getElementById("main").style.marginLeft = "0";
    }
  };

  return (
    <div className="App">
      <aside>
        <nav>
          <div id="mySidenav" className="sidenav">
            <a
              href="javascript:void(0)"
              className="closebtn"
              onClick={closeNav()}
            >
              &times;
            </a>
            <Router>
              <button>
                <Link to="/homepage">Go To Add Money Spend</Link>
              </button>
              <button>
                <Link to="/dashboard">Go To Money Record</Link>
              </button>
              <Route path="/homepage" component={Main}></Route>
              <Route path="/dashboard" component={Dashboard}></Route>
            </Router>
          </div>

          <div id="main">
            <span
              styles={{ fontSize: "30", cursor: "pointer" }}
              onClick={openNav()}
            >
              &#9776; open
            </span>
          </div>
        </nav>
      </aside>
      <main>
        <Router>
          <h1>Welcome , easily record your daily spending here!</h1>
          <Route path="/homepage" component={Main}></Route>
          <Route path="/dashboard" component={Dashboard}></Route>

          <button>
            <Link to="/">Go BACK</Link>
          </button>

          <Route exact path="/"></Route>
        </Router>
      </main>
    </div>
  );
}

