import React from "react";
import Rodal from "rodal";
import { Popover, Button, Form } from "antd";
// import "./css/dashboard.css";
import "rodal/lib/rodal.css";
import "antd/dist/antd.css";
import axios from "axios";

class AttendanceNote extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      visible: false,
      attNote: this.props.tData
    };
    this.submitAttNote = this.submitAttNote.bind(this);
    this.show = this.show.bind(this);
    this.hide = this.hide.bind(this);
    this.closeWindow = this.closeWindow.bind(this);
  }

  componentDidMount() {
    axios({
      method: "post",
      url: "https://attrc.herokuapp.com/api/dashboard/update",
      headers: {
        "Content-Type": "application/json",
        "x-auth-token": localStorage.getItem("jwtToken")
      }
    }).then(req => {
      const { attId, date, parameter, val } = req.body;
      this.setState({
        id: attId,
        date: "",
        attNote: "",
        val: ""
      });
    });
  }

  show() {
    this.setState({ visible: true });
  }

  hide() {
    this.setState({ visible: false, attNote: null });
  }

  changeAttNote = (event, date) => {
    let attNote = event.target.value;
    this.setState({ attNote: attNote });
    console.log("attnote val:", attNote);

    let attDate = date;
    attDate = this.props.value; //get the date from dashbord to this child component
    console.log("attDate:", attDate);
    this.props.onClick(attNote, attDate);
  };

  submitAttNote = (event, i) => {
    event.preventDefault();
    // event = this.props.tData.user.attendance[i].attNote;
    const updatedData = {
      id: Math.random().toString(),
      date: "",
      parameter: this.state.attNote,
      val: event
    };
    this.setState({ visible: false, tData: event });
    this.props.onSubmit(updatedData);

    console.log("substateNote", this.state.attNote);
    console.log("subpropsNote", this.props.attNote);
  };

  closeWindow = () => {
    this.setState({ visible: false, attNote: null });
  };

  content = (
    <div>
      <p>leave a note to your teacher</p>
    </div>
  );

  render() {
    return (
      <div>
        <Popover content={this.content} trigger="hover">
          <Button
            value={this.state.attNote}
            onClick={this.show}
            id="hideAttBtn"
          >
            <div
              style={{
                height: "100px",
                width: "100px",
                color: "rgba(0, 0, 0, 0.8)",
                wordWrap: "break-all "
              }}
            >
              <span> ğŸ“</span> {this.props.attNote}
              <br></br>
              ğŸ“¤{" "}
              <div
                style={{
                  height: "100px",
                  width: "100px",
                  color: "rgba(0, 0, 0, 0.8)",
                  wordWrap: "break-word "
                }}
              >
                {this.state.attNote}{" "}
              </div>
            </div>
          </Button>
        </Popover>
        <Rodal
          visible={this.state.visible}
          onClose={this.hide}
          value="write here"
        >
          <div>
            <Form id="hideAttForm" onSubmit={this.submitAttNote}>
              <h4 for="text" style={{ fontSize: "14px", color: "purple" }}>
                Please leave your message
              </h4>
              <textarea
                type="text"
                onChange={this.changeAttNote}
                onClose={this.hide}
                placeholder="write here..."
              />
              <br></br>

              <Button type="button" onClick={this.hide} value="Cancel">
                Cancel
              </Button>
              <Button type="button" value="Submit">
                Submit
              </Button>
            </Form>
          </div>
        </Rodal>
      </div>
    );
  }
}
export default AttendanceNote;
