import React from "react";
import Rodal from "rodal";
import { Popover, Button, Form } from "antd";
// import "./css/dashboard.css";
import "rodal/lib/rodal.css";
import "antd/dist/antd.css";

class Note extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      visible: false,
      note: this.props.tData
    };
    this.submitnote = this.submitnote.bind(this);
    this.show = this.show.bind(this);
    this.hide = this.hide.bind(this);
    this.closeWindow = this.closeWindow.bind(this);
  }

  show() {
    this.setState({ visible: true });
  }

  hide() {
    this.setState({ visible: false, note: null });
  }

  changenote = (event, date) => {
    let note = event.target.value;
    this.setState({ note: note });
    console.log("note val:", note);

    let attDate = date;
    attDate = this.props.value; //get the date from dashbord to this child component
    console.log("attDate:", attDate);
    this.props.onClick(note, attDate);
  };

  submitnote = (event, i) => {
    event.preventDefault();
    // event = this.props.tData.user.attendance[i].note;
    const updatedData = {
      id: Math.random().toString(),
      date: "",
      parameter: this.state.note,
      val: event
    };
    this.setState({ visible: false, tData: event });
    this.props.onSubmit(updatedData);

    console.log("substatenote", this.state.note);
    console.log("subpropsnote", this.props.note);
  };

  closeWindow = () => {
    this.setState({ visible: false, note: null });
  };

  content = (
    <div>
      <p>leave a note to your teacher</p>
    </div>
  );

  render() {
    return (
      <div>
        <Popover content={this.content} trigger="hover">
          <Button value={this.state.note} onClick={this.show} id="hideAttBtn">
            <div
              style={{
                height: "100px",
                width: "100px",
                color: "rgba(0, 0, 0, 0.8)",
                wordWrap: "break-all "
              }}
            >
              <span> ğŸ“</span> {this.props.note}
              <br></br>
              ğŸ“¤{" "}
              <div
                style={{
                  height: "100px",
                  width: "100px",
                  color: "rgba(0, 0, 0, 0.8)",
                  wordWrap: "break-word "
                }}
              >
                {this.state.note}{" "}
              </div>
            </div>
          </Button>
        </Popover>
        <Rodal
          visible={this.state.visible}
          onClose={this.hide}
          value="write here"
        >
          <div>
            <Form id="hideAttForm" onSubmit={this.submitnote}>
              <h4 for="text" style={{ fontSize: "14px", color: "purple" }}>
                Please leave your message
              </h4>
              <textarea
                type="text"
                onChange={this.changenote}
                onClose={this.hide}
                placeholder="write here..."
              />
              <br></br>

              <Button type="button" onClick={this.hide} value="Cancel">
                Cancel
              </Button>
              <Button type="button" value="Submit">
                Submit
              </Button>
            </Form>
          </div>
        </Rodal>
      </div>
    );
  }
}
export default Note;
