import React, { useState } from "react";
import NewItem from "./components/NewItem/newItem";
import ItemList from "./components/ItemList/itemList";
import "./App.css";

function App() {
  const [defaultItems, itemPrice, setItems] = useState([
    { id: 1, text: "Food & Drinks", spend: "23" },
    { id: 2, text: "entertainment & Fun", spend: "23" },
    { id: 3, text: "Trainning & Study", spend: "23" },
    { id: 4, text: "Clothes & Shoes", spend: "23" }
  ]);

  const onListHandle = newItem => {
    setItems(prevItems => prevItems.concat(newItem));
  };

  return (
    <div className="App">
      <NewItem onAddItem={onListHandle}></NewItem>
      <ItemList items={defaultItems}></ItemList>
    </div>
  );
}

export default App;
