import React, { Component } from "react";
import DateRangePicker from "@wojtekmaj/react-daterange-picker";

class DateRange extends Component {
  state = {
    // date: [new Date(), new Date()],
    startDay: "",
    endDay: "",
    dateDiff: ""
  };

  onChange = (stDate, enDate) => {
    let startDay = stDate;
    let endDay = enDate;
    let dateDiff = Math.round((endDay - startDay) / (1000 * 3600 * 24)) + 1;
    console.log(
      " olddatediff ",
      Math.round((endDay - startDay) / (1000 * 3600 * 24) + 1)
    );

    console.log("oldStart", startDay);
    console.log("oldend", endDay);

    this.setState({
      startDay: startDay,
      endDay: endDay,
      dateDiff: dateDiff
    });
    console.log("newStart", startDay);
    console.log("end", endDay);
    console.log(
      " newdatediff ",
      Math.round((endDay - startDay) / (1000 * 3600 * 24) + 1)
    );
    this.props.onChange(dateDiff, startDay, endDay);
  };

  render() {
    return (
      <div className="rangePicker" style={{ color: "blue" }}>
        <DateRangePicker
          onChange={this.onChange}
          onCalendarClose={this.onChange}
          value={this.state.date}
        />
      </div>
    );
  }
}

export default DateRange;
