import React, { Component } from "react";
import DateRangePicker from "@wojtekmaj/react-daterange-picker";

class DateRange extends Component {
  //   state = {
  //     date: [new Date(), new Date()]
  //   };

  onChange = selectDay => {
    let selectedDate = selectDay;
    let startDate = selectedDate[0];
    let endDate = selectedDate[1];
    let dateDiff = Math.round((endDate - startDate) / (1000 * 3600 * 24)) + 1;
    console.log(
      " olddatediff ",
      Math.round((endDate - startDate) / (1000 * 3600 * 24) + 1)
    );

    console.log("oldStart", startDate);
    console.log("oldend", endDate);

    this.setState({
      startDate: startDate,
      endDate: endDate,
      dateDiff: dateDiff
    });

    this.props.onChange(dateDiff, startDate, endDate);
  };

  render() {
    return (
      <div className="rangePicker" style={{ color: "blue" }}>
        <DateRangePicker onChange={this.onChange} />
      </div>
    );
  }
}

export default DateRange;
